import React, { useState, useEffect } from 'react';
import axios from 'axios';

function EmployeeDashboard() {
  const [formData, setFormData] = useState({
    type: '',
    from: '',
    to: '',
    reason: ''
  });

  const [history, setHistory] = useState([]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const fetchHistory = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/leave', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setHistory(res.data);
    } catch (err) {
      console.error('❌ Failed to load leave history:', err);
      alert('Failed to load leave history');
    }
  };

  const handleApply = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('No token found. Please login again.');
        return;
      }

      await axios.post(
        'http://localhost:5000/api/leave/apply',
        {
          leaveType: formData.type,
          fromDate: formData.from,
          toDate: formData.to,
          reason: formData.reason
        },
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );

      alert('Leave application submitted!');
      setFormData({ type: '', from: '', to: '', reason: '' });
      fetchHistory(); // Refresh history after apply
    } catch (err) {
      console.error('❌ Apply error:', err);
      alert(err.response?.data?.message || 'Failed to apply for leave');
    }
  };

  useEffect(() => {
    fetchHistory();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h1>Welcome, Employee</h1>

      <h2>Leave Balances</h2>
      <ul>
        <li>Casual Leave: 14 (dynamic if you want — let me know)</li>
        <li>Sick Leave: 12</li>
        <li>Earned Leave: 9</li>
      </ul>

      <h2>Apply for Leave</h2>
      <form onSubmit={handleApply}>
        <div>
          <label>Type:</label>
          <select name="type" value={formData.type} onChange={handleChange} required>
            <option value="">Select</option>
            <option value="casual">Casual</option>
            <option value="sick">Sick</option>
            <option value="earned">Earned</option>
          </select>
        </div>

        <div>
          <label>From:</label>
          <input type="date" name="from" value={formData.from} onChange={handleChange} required />
        </div>

        <div>
          <label>To:</label>
          <input type="date" name="to" value={formData.to} onChange={handleChange} required />
        </div>

        <div>
          <label>Reason:</label>
          <textarea name="reason" value={formData.reason} onChange={handleChange} required></textarea>
        </div>

        <button type="submit">Apply</button>
      </form>

      <h2>Leave History</h2>
      {history.length === 0 ? (
        <p>No leave applications yet.</p>
      ) : (
        <table border="1" cellPadding="5" style={{ width: '100%', marginTop: '10px' }}>
          <thead>
            <tr>
              <th>Type</th>
              <th>From</th>
              <th>To</th>
              <th>Reason</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {history.map((leave) => (
              <tr key={leave._id}>
                <td>{leave.leaveType}</td>
                <td>{new Date(leave.fromDate).toLocaleDateString()}</td>
                <td>{new Date(leave.toDate).toLocaleDateString()}</td>
                <td>{leave.reason}</td>
                <td>{leave.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default EmployeeDashboard;
