import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminPendingRequests() {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    const fetchPendingRequests = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/leave/pending', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setRequests(response.data);
      } catch (err) {
        console.error('Error fetching pending requests:', err);
        alert('Failed to load pending requests');
      }
    };

    fetchPendingRequests();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h1>Admin - Pending Leave Requests</h1>
      {requests.length === 0 ? (
        <p>No pending requests.</p>
      ) : (
        <table border="1" cellPadding="5" style={{ width: '100%', marginTop: '20px' }}>
          <thead>
            <tr>
              <th>Employee</th>
              <th>Type</th>
              <th>From</th>
              <th>To</th>
              <th>Reason</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {requests.map((req) => (
              <tr key={req._id}>
                <td>{req.userId}</td>
                <td>{req.leaveType}</td>
                <td>{req.fromDate}</td>
                <td>{req.toDate}</td>
                <td>{req.reason}</td>
                <td>{req.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AdminPendingRequests;
