import React from 'react';
import { Link } from 'react-router-dom';

function HomePage() {
  return (
    <div 
      style={{ 
        background: 'url(/your-image-path/leave-management-bg.png) no-repeat center center',
        backgroundSize: 'cover',
        minHeight: '100vh',
        color: 'white',
        position: 'relative'
      }}
    >
      {/* Top-right buttons */}
      <div style={{ 
        position: 'fixed',
        top: '20px',
        right: '20px',
        display: 'flex',
        gap: '10px'
      }}>
        <Link to="/admin-login">
          <button style={{
            padding: '8px 16px',
            fontSize: '0.9rem',
            borderRadius: '5px',
            border: 'none',
            background: '#007BFF',
            color: 'white',
            cursor: 'pointer'
          }}>
            Admin Login
          </button>
        </Link>
        <Link to="/user-login">
          <button style={{
            padding: '8px 16px',
            fontSize: '0.9rem',
            borderRadius: '5px',
            border: 'none',
            background: '#28A745',
            color: 'white',
            cursor: 'pointer'
          }}>
            User Login
          </button>
        </Link>
      </div>

      {/* Main content */}
      <div style={{ 
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '1vh',
        padding: '2rem',
        textAlign: 'center'
      }}>
        <h1 style={{ fontSize: '3rem', fontWeight: 'bold' }}>LEAVE MANAGEMENT</h1>
        <p style={{ fontSize: '1.5rem' }}>Manage Your Employee Leaves Digitally</p>
      </div>
    </div>
  );
}

export default HomePage;
