import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminPendingRequests() {
  const [requests, setRequests] = useState([]);

  // Fetch pending requests
  const fetchPendingRequests = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/leave/pending', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setRequests(response.data);
    } catch (err) {
      console.error('Error fetching pending requests:', err);
      alert('Failed to load pending requests');
    }
  };

  useEffect(() => {
    fetchPendingRequests();
  }, []);

  // Approve leave
  const handleApprove = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:5000/api/leave/${id}/approve`, {}, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      alert('Leave approved');
      fetchPendingRequests(); // Refresh the list
    } catch (err) {
      console.error('Approve error:', err);
      alert('Failed to approve leave');
    }
  };

  // Reject leave
  const handleReject = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:5000/api/leave/${id}/reject`, {}, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      alert('Leave rejected');
      fetchPendingRequests(); // Refresh the list
    } catch (err) {
      console.error('Reject error:', err);
      alert('Failed to reject leave');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Admin - Pending Leave Requests</h1>
      {requests.length === 0 ? (
        <p>No pending requests.</p>
      ) : (
        <table border="1" cellPadding="5" style={{ width: '100%', marginTop: '20px' }}>
          <thead>
            <tr>
              <th>Employee Name</th>
              <th>Employee Email</th>
              <th>Type</th>
              <th>From</th>
              <th>To</th>
              <th>Reason</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {requests.map((req) => (
              <tr key={req._id}>
                <td>{req.userId?.name || 'N/A'}</td>
                <td>{req.userId?.email || 'N/A'}</td>
                <td>{req.leaveType}</td>
                <td>{new Date(req.fromDate).toLocaleDateString()}</td>
                <td>{new Date(req.toDate).toLocaleDateString()}</td>
                <td>{req.reason}</td>
                <td>{req.status}</td>
                <td>
                  <button onClick={() => handleApprove(req._id)}>Approve</button>
                  <button onClick={() => handleReject(req._id)} style={{ marginLeft: '5px' }}>Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AdminPendingRequests;
