import React, { useEffect, useState } from 'react';
import axios from 'axios';

function EmployeeDashboard() {
  const [formData, setFormData] = useState({
    type: '',
    from: '',
    to: '',
    reason: ''
  });
  const [balance, setBalance] = useState({ casual: 0, sick: 0, earned: 0 });
  const [history, setHistory] = useState([]);

  // Fetch balance and history
  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          alert('No token found. Please login again.');
          return;
        }

        const [balRes, histRes] = await Promise.all([
          axios.get('http://localhost:5000/api/leave/balance', {
            headers: { Authorization: `Bearer ${token}` }
          }),
          axios.get('http://localhost:5000/api/leave', {
            headers: { Authorization: `Bearer ${token}` }
          })
        ]);

        setBalance(balRes.data);
        setHistory(histRes.data);
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        alert('Failed to load dashboard data');
      }
    };

    fetchData();
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleApply = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('No token found. Please login again.');
        return;
      }

      await axios.post(
        'http://localhost:5000/api/leave/apply',
        {
          leaveType: formData.type,
          fromDate: formData.from,
          toDate: formData.to,
          reason: formData.reason
        },
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );

      alert('Leave application submitted!');
      setFormData({ type: '', from: '', to: '', reason: '' });

      // Refresh balance & history
      const [balRes, histRes] = await Promise.all([
        axios.get('http://localhost:5000/api/leave/balance', {
          headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get('http://localhost:5000/api/leave', {
          headers: { Authorization: `Bearer ${token}` }
        })
      ]);
      setBalance(balRes.data);
      setHistory(histRes.data);
    } catch (err) {
      console.error('Apply error:', err.response ? err.response.data : err.message);
      alert(err.response?.data?.message || 'Failed to apply for leave');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Welcome, Employee</h1>

      <h2>Leave Balances</h2>
      <ul>
        <li>Casual Leave: {balance.casual}</li>
        <li>Sick Leave: {balance.sick}</li>
        <li>Earned Leave: {balance.earned}</li>
      </ul>

      <h2>Apply for Leave</h2>
      <form onSubmit={handleApply}>
        <div>
          <label>Type:</label>
          <select name="type" value={formData.type} onChange={handleChange} required>
            <option value="">Select</option>
            <option value="casual">Casual</option>
            <option value="sick">Sick</option>
            <option value="earned">Earned</option>
          </select>
        </div>

        <div>
          <label>From:</label>
          <input type="date" name="from" value={formData.from} onChange={handleChange} required />
        </div>

        <div>
          <label>To:</label>
          <input type="date" name="to" value={formData.to} onChange={handleChange} required />
        </div>

        <div>
          <label>Reason:</label>
          <textarea name="reason" value={formData.reason} onChange={handleChange} required></textarea>
        </div>

        <button type="submit">Apply</button>
      </form>

      <h2>Leave History</h2>
      {history.length === 0 ? (
        <p>No leave history found.</p>
      ) : (
        <table border="1" cellPadding="5" style={{ width: '100%', marginTop: '20px' }}>
          <thead>
            <tr>
              <th>Type</th>
              <th>From</th>
              <th>To</th>
              <th>Reason</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {history.map((leave) => (
              <tr key={leave._id}>
                <td>{leave.leaveType}</td>
                <td>{new Date(leave.fromDate).toLocaleDateString()}</td>
                <td>{new Date(leave.toDate).toLocaleDateString()}</td>
                <td>{leave.reason}</td>
                <td>{leave.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default EmployeeDashboard;
