import React from 'react';

const EmployeeSidebar = ({ setView }) => {
  const items = [
    { label: "Leave Summary", key: "summary" },
    { label: "Apply Leave", key: "apply" },
    { label: "Leave History", key: "history" },
  ];

  return (
    <div style={{
      width: '220px',
      backgroundColor: '#2c3e50',
      color: 'white',
      minHeight: '100vh',
      padding: '20px',
      boxShadow: '2px 0 5px rgba(0,0,0,0.1)',
      position: 'fixed',
      top: 0,
      left: 0
    }}>
      <h3 style={{ borderBottom: '1px solid gray', paddingBottom: '10px' }}>Employee Menu</h3>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {items.map(({ label, key }) => (
          <li
            key={key}
            onClick={() => setView(key)}
            style={{
              padding: '10px 0',
              cursor: 'pointer',
              borderBottom: '1px solid #444',
              transition: 'background 0.3s',
            }}
          >
            {label}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EmployeeSidebar;
