import React, { useEffect, useState } from 'react';

function EmployeeDashboard() {
  const [userName, setUserName] = useState('Employee');
  const [leaveBalance, setLeaveBalance] = useState({
    casual: 5,
    sick: 3,
    earned: 7
  });

  const [recentLeaves, setRecentLeaves] = useState([
    {
      type: 'Casual',
      from: '2025-06-10',
      to: '2025-06-12',
      reason: 'Family Function',
      status: 'Approved'
    },
    {
      type: 'Sick',
      from: '2025-06-20',
      to: '2025-06-21',
      reason: 'Fever',
      status: 'Pending'
    }
  ]);

  return (
    <div>
      <h2>Welcome, {userName}</h2>

      <div>
        <h3>Leave Balance</h3>
        <ul>
          <li>Casual Leave: {leaveBalance.casual}</li>
          <li>Sick Leave: {leaveBalance.sick}</li>
          <li>Earned Leave: {leaveBalance.earned}</li>
        </ul>
      </div>

      <div>
        <h3>Recent Leave Applications</h3>
        <table border="1" cellPadding="8">
          <thead>
            <tr>
              <th>Type</th>
              <th>From</th>
              <th>To</th>
              <th>Reason</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {recentLeaves.map((leave, index) => (
              <tr key={index}>
                <td>{leave.type}</td>
                <td>{leave.from}</td>
                <td>{leave.to}</td>
                <td>{leave.reason}</td>
                <td>{leave.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default EmployeeDashboard;
