import React, { useEffect, useState } from 'react';
import axios from 'axios';
import LeaveHistory from './LeaveHistory';
import config from '../config';



function EmployeeDashboard() {
  const [balance, setBalance] = useState({
    casual: 0,
    sick: 0,
    earned: 0
  });

  const [formData, setFormData] = useState({
    type: '',
    from: '',
    to: '',
    reason: ''
  });

  useEffect(() => {
    const fetchBalance = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${config.API_BASE_URL}${config.endpoints.leave.balance}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setBalance(res.data);
      } catch (err) {
        console.error('❌ Failed to load dashboard data:', err);
        alert('Failed to load dashboard data');
      }
    };

    fetchBalance();
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleApply = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${config.API_BASE_URL}${config.endpoints.leave.apply}`, {
        leaveType: formData.type,
        fromDate: formData.from,
        toDate: formData.to,
        reason: formData.reason
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      alert('Leave applied successfully');
      setFormData({ type: '', from: '', to: '', reason: '' });
      window.location.reload();  // reload to refresh balance
    } catch (err) {
      console.error('❌ Apply error:', err);
      alert('Failed to apply leave');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Welcome, Employee</h1>

      <h2>Leave Balances</h2>
      <ul>
        <li>Casual Leave: {balance.casual}</li>
        <li>Sick Leave: {balance.sick}</li>
        <li>Earned Leave: {balance.earned}</li>
      </ul>

      <h2>Apply for Leave</h2>
      <form onSubmit={handleApply}>
        <div>
          <label>Type:</label>
          <select name="type" value={formData.type} onChange={handleChange} required>
            <option value="">Select</option>
            <option value="casual">Casual</option>
            <option value="sick">Sick</option>
            <option value="earned">Earned</option>
          </select>
        </div>
        <div>
          <label>From:</label>
          <input type="date" name="from" value={formData.from} onChange={handleChange} required />
        </div>
        <div>
          <label>To:</label>
          <input type="date" name="to" value={formData.to} onChange={handleChange} required />
        </div>
        <div>
          <label>Reason:</label>
          <textarea name="reason" value={formData.reason} onChange={handleChange} required></textarea>
        </div>
        <button type="submit">Apply</button>
      </form>
      <LeaveHistory />
    </div>

  );
}



export default EmployeeDashboard;
