import React, { useState } from 'react';
import AdminPendingRequests from './AdminPendingRequests';
import AdminApprovedRequests from './AdminApprovedRequests';
import AdminRejectedRequests from './AdminRejectedRequests';

// ✅ Move sidebar to a separate component
const AdminSidebar = ({ setView }) => (
  <div style={{
    width: '220px',
    backgroundColor: '#2c3e50',
    color: 'white',
    minHeight: '100vh',
    padding: '20px',
    boxShadow: '2px 0 5px rgba(0,0,0,0.1)',
    position: 'fixed',
    top: 0,
    left: 0
  }}>
    <h3 style={{ borderBottom: '1px solid gray', paddingBottom: '10px' }}>Control Panel</h3>
    <ul style={{ listStyle: 'none', padding: 0 }}>
      {[
        { label: "Pending Requests", key: "pending" },
        { label: "Approved Requests", key: "approved" },
        { label: "Rejected Requests", key: "rejected" },

        { label: "View Staff", key: "viewStaff" },

      ].map(({ label, key }) => (
        <li
          key={key}
          onClick={() => setView(key)}
          style={{
            padding: '10px 0',
            cursor: 'pointer',
            borderBottom: '1px solid #444',
            transition: 'background 0.3s',
          }}
        >
          {label}
        </li>
      ))}
    </ul>
  </div>
);

// ✅ Main Dashboard Component
function AdminDashboard() {
  const [view, setView] = useState(null);

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/';
  };

  const renderContent = () => {
    switch (view) {
      case 'pending': return <AdminPendingRequests />;
      case 'approved': return <AdminApprovedRequests />;
      case 'rejected': return <AdminRejectedRequests />;

      case 'viewStaff': return <div><h3>View Staff</h3></div>;

      default: return <div><h3>Please select an option from the Control Panel</h3></div>;
    }
  };

  return (
    <div style={{ display: 'flex' }}>
      <AdminSidebar setView={setView} />

      <div style={{
        marginLeft: '220px',
        minHeight: '100vh',
        background: '#f4f4f4',
        fontFamily: 'Arial, sans-serif',
        position: 'relative',
        width: '100%'
      }}>
        <header style={{
          textAlign: 'center',
          padding: '1rem',
          backgroundColor: '#007bff',
          color: '#fff',
          fontSize: '2rem',
          fontWeight: 'bold',
          position: 'relative'
        }}>
          Admin Dashboard

          <button
            onClick={handleLogout}
            style={{
              position: 'absolute',
              right: '20px',
              top: '50%',
              transform: 'translateY(-50%)',
              backgroundColor: '#dc3545',
              color: '#fff',
              border: 'none',
              padding: '8px 16px',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '14px',
              display: 'flex',
              alignItems: 'center'
            }}
          >
            <i className="fas fa-sign-out-alt" style={{ marginRight: '6px' }}></i>
            Logout
          </button>
        </header>

        <div style={{ padding: '2rem' }}>
          {renderContent()}
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
