import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import config from '../config';
import LeaveHistory from './LeaveHistory';
import { Chart as ChartJS, BarElement, CategoryScale, LinearScale, Tooltip, Legend } from 'chart.js';

ChartJS.register(BarElement, CategoryScale, LinearScale, Tooltip, Legend);

function EmployeeDashboard() {
  const [balance, setBalance] = useState({
    casual: 0,
    sick: 0,
    earned: 0
  });

  const [formData, setFormData] = useState({
    type: '',
    from: '',
    to: '',
    reason: ''
  });

  useEffect(() => {
    const fetchBalance = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${config.API_BASE_URL}${config.endpoints.leave.balance}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setBalance(res.data);
      } catch (err) {
        console.error('❌ Failed to load dashboard data:', err);
        alert('Failed to load dashboard data');
      }
    };

    fetchBalance();
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleApply = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${config.API_BASE_URL}${config.endpoints.leave.apply}`, {
        leaveType: formData.type,
        fromDate: formData.from,
        toDate: formData.to,
        reason: formData.reason
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      alert('Leave applied successfully');
      setFormData({ type: '', from: '', to: '', reason: '' });
      window.location.reload();
    } catch (err) {
      console.error('❌ Apply error:', err);
      alert('Failed to apply leave');
    }
  };

  const total = { casual: 14, sick: 12, earned: 9 };

  const chartData = {
    labels: ['Casual', 'Sick', 'Earned'],
    datasets: [
      {
        label: 'Total Leave',
        data: [total.casual, total.sick, total.earned],
        backgroundColor: 'rgba(54, 162, 235, 0.5)'
      },
      {
        label: 'Balance',
        data: [balance.casual, balance.sick, balance.earned],
        backgroundColor: 'rgba(75, 192, 192, 0.5)'
      },
      {
        label: 'Taken',
        data: [
          total.casual - balance.casual,
          total.sick - balance.sick,
          total.earned - balance.earned
        ],
        backgroundColor: 'rgba(255, 99, 132, 0.5)'
      }
    ]
  };

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        <h1 style={{ fontWeight: 'bold', fontSize: '2rem', color: '#007bff' }}>
          Welcome, Employee
        </h1>
      </div>

      <Bar data={chartData} options={{ responsive: true, plugins: { legend: { position: 'top' } } }} />

      <h2>Apply for Leave</h2>
      <form onSubmit={handleApply}>
        <div>
          <label>Type:</label>
          <select name="type" value={formData.type} onChange={handleChange} required>
            <option value="">Select</option>
            <option value="casual">Casual</option>
            <option value="sick">Sick</option>
            <option value="earned">Earned</option>
          </select>
        </div>
        <div>
          <label>From:</label>
          <input type="date" name="from" value={formData.from} onChange={handleChange} required />
        </div>
        <div>
          <label>To:</label>
          <input type="date" name="to" value={formData.to} onChange={handleChange} required />
        </div>
        <div>
          <label>Reason:</label>
          <textarea name="reason" value={formData.reason} onChange={handleChange} required></textarea>
        </div>
        <button type="submit">Apply</button>
      </form>

      <LeaveHistory />
    </div>
  );
}

export default EmployeeDashboard;
