import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminRejectedRequests() {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    const fetchRejected = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          alert("Admin not logged in. Please login first.");
          return;
        }

        const response = await axios.get('http://localhost:5000/api/leave/rejected', {
          headers: { Authorization: `Bearer ${token}` }
        });

        setRequests(response.data);
      } catch (err) {
        console.error('Error fetching rejected requests:', err);
        alert('Failed to load rejected requests');
      }
    };
    fetchRejected();
  }, []);

  return (
    <div>
      <h2>Rejected Leave Requests</h2>
      {requests.length === 0 ? <p>No rejected requests.</p> : (
        <table border="1" cellPadding="5" style={{ width: '100%', marginTop: '20px' }}>
          <thead>
            <tr>
              <th>Employee Name</th>
              <th>Employee Email</th>
              <th>Type</th>
              <th>From</th>
              <th>To</th>
              <th>Reason</th>
            </tr>
          </thead>
          <tbody>
            {requests.map(req => (
              <tr key={req._id}>
                <td>{req.userId?.name || 'N/A'}</td>
                <td>{req.userId?.email || 'N/A'}</td>
                <td>{req.leaveType}</td>
                <td>{new Date(req.fromDate).toLocaleDateString()}</td>
                <td>{new Date(req.toDate).toLocaleDateString()}</td>
                <td>{req.reason}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AdminRejectedRequests;
