import React, { useEffect, useState } from 'react';
import axios from 'axios';
import config from '../config';
import LeaveHistory from './LeaveHistory';
import EmployeeSidebar from '../components/EmployeeSidebar';

function EmployeeDashboard() {
  const [view, setView] = useState('summary');
  const [balance, setBalance] = useState({ casual: 0, sick: 0, earned: 0 });
  const total = { casual: 14, sick: 12, earned: 9 };
  const [formData, setFormData] = useState({ type: '', from: '', to: '', reason: '' });

  useEffect(() => {
    const fetchBalance = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${config.API_BASE_URL}${config.endpoints.leave.balance}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setBalance(res.data);
      } catch (err) {
        console.error('Failed to load balance', err);
      }
    };
    fetchBalance();
  }, []);

  const handleApply = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${config.API_BASE_URL}${config.endpoints.leave.apply}`, {
        leaveType: formData.type,
        fromDate: formData.from,
        toDate: formData.to,
        reason: formData.reason
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Leave applied successfully');
      setFormData({ type: '', from: '', to: '', reason: '' });
    } catch (err) {
      alert('Failed to apply leave');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/';
  };

  return (
    <div style={{ display: 'flex', minHeight: '100vh' }}>
      <EmployeeSidebar setView={setView} />

      <div style={{ marginLeft: '220px', padding: '2rem', width: '100%' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <h1 style={{ fontSize: '2rem', color: '#007bff' }}>Welcome, Employee</h1>
          <button onClick={handleLogout} style={{
            backgroundColor: '#dc3545',
            color: '#fff',
            padding: '8px 16px',
            border: 'none',
            borderRadius: '4px'
          }}>
            Logout
          </button>
        </div>

        {view === 'summary' && (
          <>
            <h2 style={{ marginTop: '20px' }}>Leave Summary</h2>
            <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '10px', boxShadow: '0 0 10px #ccc' }}>
              <thead style={{ backgroundColor: '#007bff', color: 'white' }}>
                <tr>
                  <th>Type</th>
                  <th>Total</th>
                  <th>Taken</th>
                  <th>Balance</th>
                </tr>
              </thead>
              <tbody>
                {['casual', 'sick', 'earned'].map((type, index) => (
                  <tr key={type} style={{ backgroundColor: index % 2 ? '#f9f9f9' : '#ffffff' }}>
                    <td>{type}</td>
                    <td>{total[type]}</td>
                    <td>{total[type] - balance[type]}</td>
                    <td>{balance[type]}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </>
        )}

        {view === 'apply' && (
          <>
            <h2 style={{ marginTop: '20px' }}>Apply for Leave</h2>
            <form onSubmit={handleApply} style={{ maxWidth: '500px' }}>
              <div>
                <label>Type:</label>
                <select name="type" value={formData.type} onChange={(e) => setFormData({ ...formData, type: e.target.value })} required>
                  <option value="">Select</option>
                  <option value="casual">Casual</option>
                  <option value="sick">Sick</option>
                  <option value="earned">Earned</option>
                </select>
              </div>
              <div>
                <label>From:</label>
                <input type="date" name="from" value={formData.from} onChange={(e) => setFormData({ ...formData, from: e.target.value })} required />
              </div>
              <div>
                <label>To:</label>
                <input type="date" name="to" value={formData.to} onChange={(e) => setFormData({ ...formData, to: e.target.value })} required />
              </div>
              <div>
                <label>Reason:</label>
                <textarea name="reason" value={formData.reason} onChange={(e) => setFormData({ ...formData, reason: e.target.value })} required></textarea>
              </div>
              <button type="submit">Apply</button>
            </form>
          </>
        )}

        {view === 'history' && <LeaveHistory />}
      </div>
    </div>
  );
}

export default EmployeeDashboard;
