import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function UserLoginPage() {
  const [view, setView] = useState('login'); // 'login', 'register', or 'forgot'
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password,
      });
      if (res.data.token) {
        // ✅ Store token for future requests
        localStorage.setItem('token', res.data.token);

        // Navigate to dashboard
        navigate('/employee-dashboard');
      }
    } catch (err) {
      setMessage('Invalid login credentials');
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/auth/register', {
        name,
        email,
        password,
        role: 'employee', // Default role
      });
      setMessage('Registration successful. Please login.');
      setView('login');
    } catch (err) {
      setMessage('Registration failed. Try a different email.');
    }
  };

  const handleForgotPassword = async (e) => {
    e.preventDefault();
    try {
      // Just a placeholder action
      setMessage('Password reset link has been sent to your email.');
    } catch (err) {
      setMessage('Failed to send reset link.');
    }
  };

  return (
    <div style={{ maxWidth: '400px', margin: 'auto' }}>
      <h2>{view === 'login' ? 'Login' : view === 'register' ? 'Register' : 'Forgot Password'}</h2>

      {message && <p style={{ color: 'red' }}>{message}</p>}

      <form onSubmit={view === 'login' ? handleLogin : view === 'register' ? handleRegister : handleForgotPassword}>
        {view === 'register' && (
          <input
            type="text"
            placeholder="Full Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        )}
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        {(view === 'login' || view === 'register') && (
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        )}
        <button type="submit">
          {view === 'login' ? 'Login' : view === 'register' ? 'Register' : 'Send Reset Link'}
        </button>
      </form>

      <div style={{ marginTop: '10px' }}>
        {view !== 'login' && <button onClick={() => setView('login')}>Back to Login</button>}
        {view === 'login' && (
          <>
            <button onClick={() => setView('register')}>New Registration</button>
            <button onClick={() => setView('forgot')}>Forgot Password?</button>
          </>
        )}
      </div>
    </div>
  );
}

export default UserLoginPage;
