import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import config from '../config';
import LeaveHistory from './LeaveHistory';
import { Chart as ChartJS, BarElement, CategoryScale, LinearScale, Tooltip, Legend } from 'chart.js';

ChartJS.register(BarElement, CategoryScale, LinearScale, Tooltip, Legend);

function EmployeeDashboard() {
  const [balance, setBalance] = useState({
    casual: 0,
    sick: 0,
    earned: 0
  });

  const [formData, setFormData] = useState({
    type: '',
    from: '',
    to: '',
    reason: ''
  });

  useEffect(() => {
    const fetchBalance = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${config.API_BASE_URL}${config.endpoints.leave.balance}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setBalance(res.data);
      } catch (err) {
        console.error('❌ Failed to load dashboard data:', err);
        alert('Failed to load dashboard data');
      }
    };

    fetchBalance();
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleApply = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${config.API_BASE_URL}${config.endpoints.leave.apply}`, {
        leaveType: formData.type,
        fromDate: formData.from,
        toDate: formData.to,
        reason: formData.reason
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      alert('Leave applied successfully');
      setFormData({ type: '', from: '', to: '', reason: '' });
      window.location.reload();
    } catch (err) {
      console.error('❌ Apply error:', err);
      alert('Failed to apply leave');
    }
  };

  const total = { casual: 14, sick: 12, earned: 9 };

  const chartData = {
    labels: ['Casual', 'Sick', 'Earned'],
    datasets: [
      {
        label: 'Total Leave',
        data: [total.casual, total.sick, total.earned],
        backgroundColor: 'rgba(54, 162, 235, 0.5)'
      },
      {
        label: 'Balance',
        data: [balance.casual, balance.sick, balance.earned],
        backgroundColor: 'rgba(75, 192, 192, 0.5)'
      },
      {
        label: 'Taken',
        data: [
          total.casual - balance.casual,
          total.sick - balance.sick,
          total.earned - balance.earned
        ],
        backgroundColor: 'rgba(255, 99, 132, 0.5)'
      }
    ]
  };

  return (
    <div style={{ padding: '20px' }}>
      {/* Logout Button */}
      <div style={{ position: 'absolute', top: '20px', right: '20px' }}>
        <button 
          onClick={() => {
            localStorage.removeItem('token');
            window.location.href = '/';
          }}
          style={{
            padding: '8px 16px',
            backgroundColor: '#dc3545',
            color: '#fff',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Logout
        </button>
      </div>

      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        <h1 style={{ fontWeight: 'bold', fontSize: '2.5rem', color: '#007bff' }}>
          Welcome, Employee
        </h1>
      </div>

      <div style={{ maxWidth: '700px', margin: '0 auto' }}>
        <Bar data={chartData} options={{ responsive: true, plugins: { legend: { position: 'top' } } }} />
      </div>

      <div style={{ marginTop: '30px' }}>
        <h2>Apply for Leave</h2>
        <form onSubmit={handleApply} style={{ maxWidth: '500px', margin: '0 auto' }}>
          <div style={{ marginBottom: '10px' }}>
            <label>Type:</label><br />
            <select name="type" value={formData.type} onChange={handleChange} required style={{ width: '100%', padding: '8px' }}>
              <option value="">Select</option>
              <option value="casual">Casual</option>
              <option value="sick">Sick</option>
              <option value="earned">Earned</option>
            </select>
          </div>
          <div style={{ marginBottom: '10px' }}>
            <label>From:</label><br />
            <input type="date" name="from" value={formData.from} onChange={handleChange} required style={{ width: '100%', padding: '8px' }} />
          </div>
          <div style={{ marginBottom: '10px' }}>
            <label>To:</label><br />
            <input type="date" name="to" value={formData.to} onChange={handleChange} required style={{ width: '100%', padding: '8px' }} />
          </div>
          <div style={{ marginBottom: '10px' }}>
            <label>Reason:</label><br />
            <textarea name="reason" value={formData.reason} onChange={handleChange} required style={{ width: '100%', padding: '8px' }}></textarea>
          </div>
          <button type="submit" style={{ padding: '10px 20px', backgroundColor: '#28a745', color: '#fff', border: 'none', borderRadius: '4px' }}>
            Apply
          </button>
        </form>
      </div>

      <LeaveHistory />
    </div>
  );
}

export default EmployeeDashboard;
