import React, { useEffect, useState } from 'react';
import axios from 'axios';
import LeaveHistory from './LeaveHistory';
import config from '../config';

function EmployeeDashboard() {
  const [balance, setBalance] = useState({
    casual: 0,
    sick: 0,
    earned: 0
  });

  const total = { casual: 14, sick: 12, earned: 9 };

  const [formData, setFormData] = useState({
    type: '',
    from: '',
    to: '',
    reason: ''
  });

  useEffect(() => {
    const fetchBalance = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${config.API_BASE_URL}${config.endpoints.leave.balance}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setBalance(res.data);
      } catch (err) {
        console.error('❌ Failed to load dashboard data:', err);
        alert('Failed to load dashboard data');
      }
    };
    fetchBalance();
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleApply = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${config.API_BASE_URL}${config.endpoints.leave.apply}`, {
        leaveType: formData.type,
        fromDate: formData.from,
        toDate: formData.to,
        reason: formData.reason
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      alert('Leave applied successfully');
      setFormData({ type: '', from: '', to: '', reason: '' });
      window.location.reload(); 
    } catch (err) {
      console.error('❌ Apply error:', err);
      alert('Failed to apply leave');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/';
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h1 style={{ textAlign: 'center', flexGrow: 1 }}>Welcome, Employee</h1>
        <button onClick={handleLogout} style={{
          padding: '8px 16px',
          backgroundColor: '#dc3545',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer'
        }}>
          Logout
        </button>
      </div>

      <h2>Leave Summary</h2>
      <table border="1" cellPadding="8" style={{ width: '100%', borderCollapse: 'collapse', textAlign: 'center' }}>
        <thead style={{ backgroundColor: '#f2f2f2' }}>
          <tr>
            <th>Leave Type</th>
            <th>Total Leave</th>
            <th>Leave Taken</th>
            <th>Balance</th>
          </tr>
        </thead>
        <tbody>
          {['casual', 'sick', 'earned'].map((type) => (
            <tr key={type}>
              <td style={{ textTransform: 'capitalize' }}>{type}</td>
              <td>{total[type]}</td>
              <td>{total[type] - balance[type]}</td>
              <td>{balance[type]}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2 style={{ marginTop: '30px' }}>Apply for Leave</h2>
      <form onSubmit={handleApply} style={{ maxWidth: '400px' }}>
        <div>
          <label>Type:</label>
          <select name="type" value={formData.type} onChange={handleChange} required style={{ width: '100%', padding: '8px', margin: '5px 0' }}>
            <option value="">Select</option>
            <option value="casual">Casual</option>
            <option value="sick">Sick</option>
            <option value="earned">Earned</option>
          </select>
        </div>
        <div>
          <label>From:</label>
          <input type="date" name="from" value={formData.from} onChange={handleChange} required style={{ width: '100%', padding: '8px', margin: '5px 0' }} />
        </div>
        <div>
          <label>To:</label>
          <input type="date" name="to" value={formData.to} onChange={handleChange} required style={{ width: '100%', padding: '8px', margin: '5px 0' }} />
        </div>
        <div>
          <label>Reason:</label>
          <textarea name="reason" value={formData.reason} onChange={handleChange} required style={{ width: '100%', padding: '8px', margin: '5px 0' }}></textarea>
        </div>
        <button type="submit" style={{
          padding: '10px 20px',
          backgroundColor: '#28a745',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer',
          width: '100%'
        }}>
          Apply
        </button>
      </form>

      <div style={{ marginTop: '30px' }}>
        <LeaveHistory />
      </div>
    </div>
  );
}

export default EmployeeDashboard;
