import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminDashboard() {
  const [leaves, setLeaves] = useState([]);

  useEffect(() => {
    const fetchLeaves = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/leaves');
        setLeaves(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchLeaves();
  }, []);

  const updateStatus = async (id, status) => {
    try {
      await axios.put(`http://localhost:5000/api/leaves/${id}`, { status });
      setLeaves((prev) =>
        prev.map((leave) =>
          leave._id === id ? { ...leave, status } : leave
        )
      );
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div>
      <h2>Admin - Approve/Reject Leave Requests</h2>
      {leaves.length === 0 ? (
        <p>No leave requests found.</p>
      ) : (
        leaves.map((leave) => (
          <div key={leave._id} style={{ border: '1px solid black', margin: '10px', padding: '10px' }}>
            <p><strong>User:</strong> {leave.userEmail}</p>
            <p><strong>From:</strong> {leave.fromDate}</p>
            <p><strong>To:</strong> {leave.toDate}</p>
            <p><strong>Reason:</strong> {leave.reason}</p>
            <p><strong>Status:</strong> {leave.status}</p>
            <button onClick={() => updateStatus(leave._id, 'approved')}>Approve</button>
            <button onClick={() => updateStatus(leave._id, 'rejected')}>Reject</button>
          </div>
        ))
      )}
    </div>
  );
}

export default AdminDashboard;
