// src/pages/EmployeeDashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './EmployeeDashboard.css';

function EmployeeDashboard() {
  const [user, setUser] = useState(null);
  const [leaves, setLeaves] = useState([]);
  const [balance, setBalance] = useState({ casual: 0, sick: 0, earned: 0 });

  useEffect(() => {
    // Replace with your actual auth token logic
    const token = localStorage.getItem('token');
    axios.get('http://localhost:5000/api/user/profile', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => setUser(res.data))
    .catch(err => console.error(err));

    axios.get('http://localhost:5000/api/leaves/mine', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => {
      setLeaves(res.data);
      calculateBalance(res.data);
    });
  }, []);

  const calculateBalance = (leaves) => {
    const used = { casual: 0, sick: 0, earned: 0 };
    leaves.forEach(leave => {
      if (leave.status === 'Approved') {
        used[leave.type] += leave.days;
      }
    });
    const max = { casual: 12, sick: 8, earned: 10 };
    setBalance({
      casual: max.casual - used.casual,
      sick: max.sick - used.sick,
      earned: max.earned - used.earned
    });
  };

  return (
    <div className="dashboard">
      <h2>Welcome, {user?.name || 'Employee'} ðŸ‘‹</h2>

      <section className="leave-balance">
        <h3>Leave Balance</h3>
        <ul>
          <li>Casual: {balance.casual} days</li>
          <li>Sick: {balance.sick} days</li>
          <li>Earned: {balance.earned} days</li>
        </ul>
      </section>

      <section className="leave-history">
        <h3>Recent Leave Requests</h3>
        <table>
          <thead>
            <tr>
              <th>Type</th>
              <th>From</th>
              <th>To</th>
              <th>Status</th>
              <th>Reason</th>
            </tr>
          </thead>
          <tbody>
            {leaves.slice(-5).reverse().map((leave, i) => (
              <tr key={i}>
                <td>{leave.type}</td>
                <td>{leave.fromDate}</td>
                <td>{leave.toDate}</td>
                <td className={`status ${leave.status.toLowerCase()}`}>{leave.status}</td>
                <td>{leave.reason}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
}

export default EmployeeDashboard;
