import React, { useState } from 'react';
import AdminPendingRequests from './AdminPendingRequests';  // Make sure the path is correct

function AdminDashboard() {
  const [view, setView] = useState(null);  // 'pending', 'approved', 'rejected'

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Admin Dashboard</h1>
      <div style={{ marginTop: '1rem' }}>
        <button onClick={() => setView('pending')}>Pending Requests</button><br /><br />
        <button onClick={() => setView('approved')}>Approved Requests</button><br /><br />
        <button onClick={() => setView('rejected')}>Rejected Requests</button><br /><br />
      </div>

      {view === 'pending' && <AdminPendingRequests />}
      {/* You can add ApprovedRequests / RejectedRequests components similarly */}
    </div>
  );
}

export default AdminDashboard;
