import React, { useState } from 'react';
import axios from 'axios';

function ApplyLeave() {
  const [leave, setLeave] = useState({
    leaveType: '',
    fromDate: '',
    toDate: '',
    reason: ''
  });

  const handleSubmit = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5000/api/leave', leave, {
        headers: { Authorization: 'Bearer ' + token }
      });
      alert('Leave applied successfully');
    } catch {
      alert('Failed to apply for leave');
    }
  };

  return (
    <div>
      <h2>Apply Leave</h2>
      <input placeholder="Leave Type" onChange={e => setLeave({ ...leave, leaveType: e.target.value })} />
      <input type="date" onChange={e => setLeave({ ...leave, fromDate: e.target.value })} />
      <input type="date" onChange={e => setLeave({ ...leave, toDate: e.target.value })} />
      <textarea placeholder="Reason" onChange={e => setLeave({ ...leave, reason: e.target.value })}></textarea>
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
}

export default ApplyLeave;
