import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminApprovedRequests() {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    const fetchApproved = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/leave/approved', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setRequests(response.data);
      } catch (err) {
        console.error('Error fetching approved requests:', err);
        alert('Failed to load approved requests');
      }
    };
    fetchApproved();
  }, []);

  return (
    <div>
      <h2>Approved Leave Requests</h2>
      {requests.length === 0 ? <p>No approved requests.</p> : (
        <table border="1">
          <thead>
            <tr>
              <th>Name</th><th>Email</th><th>Type</th><th>From</th><th>To</th><th>Reason</th>
            </tr>
          </thead>
          <tbody>
            {requests.map(req => (
              <tr key={req._id}>
                <td>{req.userId?.name}</td>
                <td>{req.userId?.email}</td>
                <td>{req.leaveType}</td>
                <td>{new Date(req.fromDate).toLocaleDateString()}</td>
                <td>{new Date(req.toDate).toLocaleDateString()}</td>
                <td>{req.reason}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AdminApprovedRequests;
