import React, { useEffect, useState } from 'react';
import axios from 'axios';
import LeaveHistory from './LeaveHistory';
import config from '../config';
import EmployeeSidebar from '../components/EmployeeSidebar';

function EmployeeDashboard() {
  const [balance, setBalance] = useState({ casual: 0, sick: 0, earned: 0 });
  const [formData, setFormData] = useState({ type: '', from: '', to: '', reason: '' });
  const [view, setView] = useState('summary'); // default to summary
  const total = { casual: 14, sick: 12, earned: 9 };

  useEffect(() => {
    const fetchBalance = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${config.API_BASE_URL}${config.endpoints.leave.balance}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setBalance(res.data);
      } catch (err) {
        console.error('❌ Failed to load dashboard data:', err);
        alert('Failed to load dashboard data');
      }
    };
    fetchBalance();
  }, []);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleApply = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post(`${config.API_BASE_URL}${config.endpoints.leave.apply}`, {
        leaveType: formData.type,
        fromDate: formData.from,
        toDate: formData.to,
        reason: formData.reason
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Leave applied successfully');
      setFormData({ type: '', from: '', to: '', reason: '' });
      window.location.reload();
    } catch (err) {
      console.error('❌ Apply error:', err);
      alert('Failed to apply leave');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/';
  };

  const renderContent = () => {
    switch (view) {
      case 'summary':
        return (
          <>
            <h2>Leave Summary</h2>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead style={{ backgroundColor: '#007bff', color: 'white' }}>
                <tr>
                  <th>Leave Type</th>
                  <th>Total</th>
                  <th>Taken</th>
                  <th>Balance</th>
                </tr>
              </thead>
              <tbody>
                {['casual', 'sick', 'earned'].map(type => (
                  <tr key={type}>
                    <td style={{ textTransform: 'capitalize' }}>{type}</td>
                    <td>{total[type]}</td>
                    <td>{total[type] - balance[type]}</td>
                    <td>{balance[type]}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </>
        );
      case 'apply':
        return (
          <>
            <h2>Apply for Leave</h2>
            <form onSubmit={handleApply} style={{ maxWidth: '500px' }}>
              <div><label>Type:</label>
                <select name="type" value={formData.type} onChange={handleChange} required>
                  <option value="">Select</option>
                  <option value="casual">Casual</option>
                  <option value="sick">Sick</option>
                  <option value="earned">Earned</option>
                </select>
              </div>
              <div><label>From:</label><input type="date" name="from" value={formData.from} onChange={handleChange} required /></div>
              <div><label>To:</label><input type="date" name="to" value={formData.to} onChange={handleChange} required /></div>
              <div><label>Reason:</label><textarea name="reason" value={formData.reason} onChange={handleChange} required /></div>
              <button type="submit">Apply</button>
            </form>
          </>
        );
      case 'history':
        return <LeaveHistory />;
      default:
        return <h3>Please select a section</h3>;
    }
  };

  return (
    <div style={{ display: 'flex' }}>
      <EmployeeSidebar setView={setView} />
      <div style={{ marginLeft: '220px', padding: '20px', width: '100%' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <h1 style={{ fontWeight: 'bold', color: '#007bff' }}><i className="fas fa-user"></i> Welcome, Employee</h1>
          <button onClick={handleLogout} style={{
            padding: '8px 16px',
            backgroundColor: '#dc3545',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}>
            <i className="fas fa-sign-out-alt"></i> Logout
          </button>
        </div>
        <div style={{ marginTop: '30px' }}>
          {renderContent()}
        </div>
      </div>
    </div>
  );
}

export default EmployeeDashboard;
