import React from 'react';
import { Link } from 'react-router-dom';

function HomePage() {
  return (
    <div 
      style={{ 
        background: 'url(/your-image-path/leave-management-bg.png) no-repeat center center fixed',
        backgroundSize: 'cover',
        minHeight: '100vh',
        color: 'white',
        paddingTop: '80px',  // space for the fixed buttons
        textAlign: 'center'
      }}
    >
      <div 
        style={{
          position: 'fixed',
          top: '20px',
          left: '50%',
          transform: 'translateX(-50%)',
          display: 'flex',
          gap: '20px',
          zIndex: 1000
        }}
      >
        <Link to="/admin-login">
          <button style={{
            padding: '10px 20px',
            fontSize: '1rem',
            borderRadius: '5px',
            border: 'none',
            background: '#007BFF',
            color: 'white',
            cursor: 'pointer'
          }}>
            Admin Login
          </button>
        </Link>
        <Link to="/user-login">
          <button style={{
            padding: '10px 20px',
            fontSize: '1rem',
            borderRadius: '5px',
            border: 'none',
            background: '#28A745',
            color: 'white',
            cursor: 'pointer'
          }}>
            User Login
          </button>
        </Link>
      </div>


    </div>
  );
}

export default HomePage;
