import React, { useState } from 'react';
import AdminPendingRequests from './AdminPendingRequests';
import AdminApprovedRequests from './AdminApprovedRequests';
import AdminRejectedRequests from './AdminRejectedRequests';
import AdminSidebar from '../components/AdminSidebar';

function AdminDashboard() {
  const [view, setView] = useState(null);

  return (
    <div style={{ display: 'flex' }}>
      {/* Sidebar on the left */}
      <AdminSidebar setView={setView} />

      {/* Main content area */}
      <div style={{ marginLeft: '220px', padding: '20px', width: '100%', background: '#f4f4f4', minHeight: '100vh' }}>
        <header style={{
          position: 'relative',
          backgroundColor: '#007bff',
          color: '#fff',
          padding: '1rem',
          textAlign: 'center',
          fontSize: '2rem',
          fontWeight: 'bold'
        }}>
          Admin Dashboard

          {/* Logout button */}
          <button
            onClick={() => {
              localStorage.removeItem('token');
              window.location.href = '/';
            }}
            style={{
              position: 'absolute',
              right: '20px',
              top: '50%',
              transform: 'translateY(-50%)',
              backgroundColor: '#dc3545',
              color: '#fff',
              border: 'none',
              padding: '8px 16px',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '14px',
              display: 'flex',
              alignItems: 'center'
            }}
          >
            <i className="fas fa-sign-out-alt" style={{ marginRight: '6px' }}></i>
            Logout
          </button>
        </header>

        {/* Content based on selected view */}
        <div style={{ padding: '2rem' }}>
          {view === 'pending' && <AdminPendingRequests />}
          {view === 'approved' && <AdminApprovedRequests />}
          {view === 'rejected' && <AdminRejectedRequests />}
          {!view && <p>Please select an option from the menu.</p>}
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
